mapper.insert:
INSERT INTO feedback(
    member_id
    , memo_id
    , reaction
)
VALUES(
    #{sendId}
    , #{memoId}
    , #{reaction}
)

mapper.select:
SELECT status
    , id AS reactionId
    , reaction
FROM feedback
WHERE memo_id = #{memoId}
    AND member_id = #{sendId}

mapper.update:
UPDATE feedback
SET reaction = #{reaction}, status = true
WHERE id = #{reactionId}

UPDATE feedback
SET status = false
WHERE id = #{reactionId}
