mapper.insert:
INSERT INTO member(
    user_id
, nickname
, name
, password
, email
, role
)
Values(
    #{userId}
, #{nickname}
, #{name}
, #{password}
, #{email}
, #{role}
)

mapper.select:
SELECT role
FROM member
WHERE user_id = #{userId}

mapper.update:
UPDATE member
SET nickname = #{request.nickname}
    , profile_photo_url = #{request.photoUrl}
WHERE user_id = #{userId}
